package ListaDeExercicios2;

public class Exercicios9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numMenor = 1, numMaior = 300 ;
		
		System.out.println("Forma Crescente");
		while(numMenor <= 300 ) {
			System.out.println(numMenor);
			numMenor += 1;
		}
		System.out.println("\nFORMA DESCRESCENTE");
		while(numMaior >=1) {
			System.out.println(numMaior);
			numMaior-- ;
		}


	}

}
